mapper.select:
SELECT COUNT(*)
FROM BOARD
WHERE BCLASS = #{bClass}

SELECT * FROM (SELECT ROWNUM AS RN, A.* FROM (SELECT BNO, MID, BTITLE, BCONTENT, BPW, BHIT, BFILENAME, BREF, BSTEP, BDEPTH, BCLASS, BREGDATE FROM BOARD WHERE BCLASS = #{param1}
AND BTITLE LIKE CONCAT('%', CONCAT(#{param5}, '%'))
AND BCONTENT LIKE CONCAT('%', CONCAT(#{param5}, '%'))
AND MID LIKE CONCAT('%', CONCAT(#{param5}, '%'))
AND ( BTITLE LIKE CONCAT('%', CONCAT(#{param5}, '%')) OR BCONTENT LIKE CONCAT('%', CONCAT(#{param5}, '%')) )
ORDER BY BREF DESC, BDEPTH ASC, BSTEP DESC) A ) WHERE RN BETWEEN #{param2} AND #{param3}

UPDATE BOARD
SET BHIT = BHIT + 1
WHERE BNO = #{bNo}

mapper.update:
UPDATE BOARD
SET BTITLE = #{bTitle}, BCONTENT = #{bContent}

	, BFILENAME = #{bFilename}

WHERE BNO = #{bNo}
