mapper.select:
SELECT *
FROM MEMBERS
WHERE MID = #{mId} AND MPW = #{mPw}

mapper.update:
UPDATE MEMBERS
SET MNAME = #{param1}, MEMAIL = #{param2}, MPHONE = #{param3}, MADDR = #{param4}
WHERE MNO =#{param5}

UPDATE PRODUCTS
SET PPRICE = #{param1}
WHERE PNO =#{param2}

UPDATE PRODUCTS
SET PDISRATE = #{param1}
WHERE PNO =#{param2}

UPDATE MEMBERS
SET MGRADE = #{param1}
WHERE MNO = #{param2}

UPDATE MEMBERS
SET MPOINT = NVL(MPOINT, 0) + ${param1}
WHERE MNO = #{param2}

UPDATE MEMBERS
SET MPW = #{param1}
WHERE MID = #{param2}
