mapper.select:
SELECT MNO, MNAME, MADDR, MEMAIL, MPHONE, MPAYMENT
FROM MEMBERS
WHERE MID = #{mId}

mapper.update:
UPDATE MEMBERS
SET MPOINT = NVL(MPOINT, 0) - #{param1}
WHERE MNO = #{param2}

UPDATE MEMBERS
SET MPOINT = NVL(MPOINT, 0) + #{param1}
WHERE MNO = #{param2}

UPDATE MEMBERS
SET MGRADE = #{param2}
WHERE MID = #{param1}

UPDATE STOCK
SET SAMOUNT = SAMOUNT - #{param3}
WHERE PNO = #{param1} AND PSIZE = #{param2}

mapper.select:
SELECT NVL(SAMOUNT, 0)
FROM STOCK
WHERE PNO = #{param1} AND PSIZE = #{param2}
