mapper.select:
SELECT PNO, PNAME, PFILENAME, PPRICE, PCATEGORY, PCATEGORY_SUB, PBRAND,( SELECT AVG( RRATING ) FROM REVIEWS R WHERE P.PNO = R.PNO ) AS PRATING, PDISRATE 
   FROM PRODUCTS P


         PCATEGORY = ${pCategory}


         PCATEGORY_SUB = ${pCategory_sub}


            PBRAND = ${pBrand}


   ORDER BY PNO DESC

    	SELECT SAMOUNT
		FROM STOCK 
		WHERE PSIZE = #{param1}
		AND PNO = #{param2}
